#!/bin/sh

include_dir=../../include/openssl

cat > "$***REMOVED***include_dir***REMOVED***/stack_macros.h" << EOF
/* Copyright (c) 2014, Google Inc.
 *
 * Permission to use, copy, modify, and/or distribute this software for any
 * purpose with or without fee is hereby granted, provided that the above
 * copyright notice and this permission notice appear in all copies.
 *
 * THE SOFTWARE IS PROVIDED "AS IS" AND THE AUTHOR DISCLAIMS ALL WARRANTIES
 * WITH REGARD TO THIS SOFTWARE INCLUDING ALL IMPLIED WARRANTIES OF
 * MERCHANTABILITY AND FITNESS. IN NO EVENT SHALL THE AUTHOR BE LIABLE FOR ANY
 * SPECIAL, DIRECT, INDIRECT, OR CONSEQUENTIAL DAMAGES OR ANY DAMAGES
 * WHATSOEVER RESULTING FROM LOSS OF USE, DATA OR PROFITS, WHETHER IN AN ACTION
 * OF CONTRACT, NEGLIGENCE OR OTHER TORTIOUS ACTION, ARISING OUT OF OR IN
 * CONNECTION WITH THE USE OR PERFORMANCE OF THIS SOFTWARE. */

#if !defined(IN_STACK_H)
#error "Don't include this file directly. Include stack.h."
#endif

EOF

output_stack () ***REMOVED***
  type=$1
  ptrtype=$2

  cat >> "$***REMOVED***include_dir***REMOVED***/stack_macros.h" << EOF
/* $***REMOVED***type***REMOVED*** */
#define sk_$***REMOVED***type***REMOVED***_new(comp)\\
  ((STACK_OF($***REMOVED***type***REMOVED***)*) sk_new(CHECKED_CAST(stack_cmp_func, int (*) (const $***REMOVED***ptrtype***REMOVED*** *a, const $***REMOVED***ptrtype***REMOVED*** *b), comp)))

#define sk_$***REMOVED***type***REMOVED***_new_null()\\
  ((STACK_OF($***REMOVED***type***REMOVED***)*) sk_new_null())

#define sk_$***REMOVED***type***REMOVED***_num(sk)\\
  sk_num(CHECKED_CAST(const _STACK*, const STACK_OF($***REMOVED***type***REMOVED***)*, sk))

#define sk_$***REMOVED***type***REMOVED***_zero(sk)\\
  sk_zero(CHECKED_CAST(_STACK*, STACK_OF($***REMOVED***type***REMOVED***)*, sk));

#define sk_$***REMOVED***type***REMOVED***_value(sk, i)\\
  (($***REMOVED***ptrtype***REMOVED***) sk_value(CHECKED_CAST(const _STACK*, const STACK_OF($***REMOVED***type***REMOVED***)*, sk), (i)))

#define sk_$***REMOVED***type***REMOVED***_set(sk, i, p)\\
  (($***REMOVED***ptrtype***REMOVED***) sk_set(CHECKED_CAST(_STACK*, STACK_OF($***REMOVED***type***REMOVED***)*, sk), (i), CHECKED_CAST(void*, $***REMOVED***ptrtype***REMOVED***, p)))

#define sk_$***REMOVED***type***REMOVED***_free(sk)\\
  sk_free(CHECKED_CAST(_STACK*, STACK_OF($***REMOVED***type***REMOVED***)*, sk))

#define sk_$***REMOVED***type***REMOVED***_pop_free(sk, free_func)\\
  sk_pop_free(CHECKED_CAST(_STACK*, STACK_OF($***REMOVED***type***REMOVED***)*, sk), CHECKED_CAST(void (*) (void*), void (*) ($***REMOVED***ptrtype***REMOVED***), free_func))

#define sk_$***REMOVED***type***REMOVED***_insert(sk, p, where)\\
  sk_insert(CHECKED_CAST(_STACK*, STACK_OF($***REMOVED***type***REMOVED***)*, sk), CHECKED_CAST(void*, $***REMOVED***ptrtype***REMOVED***, p), (where))

#define sk_$***REMOVED***type***REMOVED***_delete(sk, where)\\
  (($***REMOVED***ptrtype***REMOVED***) sk_delete(CHECKED_CAST(_STACK*, STACK_OF($***REMOVED***type***REMOVED***)*, sk), (where)))

#define sk_$***REMOVED***type***REMOVED***_delete_ptr(sk, p)\\
  (($***REMOVED***ptrtype***REMOVED***) sk_delete_ptr(CHECKED_CAST(_STACK*, STACK_OF($***REMOVED***type***REMOVED***)*, sk), CHECKED_CAST(void*, $***REMOVED***ptrtype***REMOVED***, p)))

#define sk_$***REMOVED***type***REMOVED***_find(sk, out_index, p)\\
  sk_find(CHECKED_CAST(_STACK*, STACK_OF($***REMOVED***type***REMOVED***)*, sk), (out_index), CHECKED_CAST(void*, $***REMOVED***ptrtype***REMOVED***, p))

#define sk_$***REMOVED***type***REMOVED***_shift(sk)\\
  (($***REMOVED***ptrtype***REMOVED***) sk_shift(CHECKED_CAST(_STACK*, STACK_OF($***REMOVED***type***REMOVED***)*, sk)))

#define sk_$***REMOVED***type***REMOVED***_push(sk, p)\\
  sk_push(CHECKED_CAST(_STACK*, STACK_OF($***REMOVED***type***REMOVED***)*, sk), CHECKED_CAST(void*, $***REMOVED***ptrtype***REMOVED***, p))

#define sk_$***REMOVED***type***REMOVED***_pop(sk)\\
  (($***REMOVED***ptrtype***REMOVED***) sk_pop(CHECKED_CAST(_STACK*, STACK_OF($***REMOVED***type***REMOVED***)*, sk)))

#define sk_$***REMOVED***type***REMOVED***_dup(sk)\\
  ((STACK_OF($***REMOVED***type***REMOVED***)*) sk_dup(CHECKED_CAST(const _STACK*, const STACK_OF($***REMOVED***type***REMOVED***)*, sk)))

#define sk_$***REMOVED***type***REMOVED***_sort(sk)\\
  sk_sort(CHECKED_CAST(_STACK*, STACK_OF($***REMOVED***type***REMOVED***)*, sk))

#define sk_$***REMOVED***type***REMOVED***_is_sorted(sk)\\
  sk_is_sorted(CHECKED_CAST(const _STACK*, const STACK_OF($***REMOVED***type***REMOVED***)*, sk))

#define sk_$***REMOVED***type***REMOVED***_set_cmp_func(sk, comp)\\
  ((int (*) (const $***REMOVED***type***REMOVED*** **a, const $***REMOVED***type***REMOVED*** **b)) sk_set_cmp_func(CHECKED_CAST(_STACK*, STACK_OF($***REMOVED***type***REMOVED***)*, sk), CHECKED_CAST(stack_cmp_func, int (*) (const $***REMOVED***type***REMOVED*** **a, const $***REMOVED***type***REMOVED*** **b), comp)))

#define sk_$***REMOVED***type***REMOVED***_deep_copy(sk, copy_func, free_func)\\
((STACK_OF($***REMOVED***type***REMOVED***)*) sk_deep_copy(CHECKED_CAST(const _STACK*, const STACK_OF($***REMOVED***type***REMOVED***)*, sk), CHECKED_CAST(void* (*) (void*), $***REMOVED***ptrtype***REMOVED*** (*) ($***REMOVED***ptrtype***REMOVED***), copy_func), CHECKED_CAST(void (*) (void*), void (*) ($***REMOVED***ptrtype***REMOVED***), free_func)))

EOF
***REMOVED***

stack_types=$(cat "$***REMOVED***include_dir***REMOVED***/stack.h" | grep '^ \* STACK_OF:' | sed -e 's/.*STACK_OF://' -e 's/ .*//')
const_stack_types=$(cat "$***REMOVED***include_dir***REMOVED***/stack.h" | grep '^ \* CONST_STACK_OF:' | sed -e 's/.*CONST_STACK_OF://' -e 's/ .*//')
special_stack_types=$(cat "$***REMOVED***include_dir***REMOVED***/stack.h" | grep '^ \* SPECIAL_STACK_OF:' | sed -e 's/.*SPECIAL_STACK_OF://' -e 's/ .*//')

for type in $stack_types; do
  echo Stack of $***REMOVED***type***REMOVED***
  output_stack "$***REMOVED***type***REMOVED***" "$***REMOVED***type***REMOVED*** *"
done

for type in $const_stack_types; do
  echo Stack of $***REMOVED***type***REMOVED***
  output_stack "$***REMOVED***type***REMOVED***" "const $***REMOVED***type***REMOVED*** *"
done

for type in $special_stack_types; do
  echo Stack of $***REMOVED***type***REMOVED***
  output_stack "$***REMOVED***type***REMOVED***" "$***REMOVED***type***REMOVED***"
done

clang-format -i "$***REMOVED***include_dir***REMOVED***/stack_macros.h"
